<script>

export default {

  data() {
    return {
      userInfo: {
        photo: "",
        name: ""
      }
    };
  },

  methods: {
    handleCommand(cmd) {
      if (cmd == "logout") {
        //怎么判断你有没有登录？
        //就看你有没有localStorage
        //如果有，代表登录了
        //如果没有代表没登录
        //所以如果要做退出其实就是把localStorage的数据删掉，就代表你退出了
        window.localStorage.removeItem("userInfo");
        //退出
        this.$router.push("/login");
      } else if( cmd == 'addr'){

        //怎么一个新的页面？
        window.open('https://github.com/hmzll/topline-admin-39')
      }
    }
  },

  beforeCreate() {
    //判断是否登录：判断依据是看你有没有localStorage
    // var res = window.localStorage.getItem('userInfo')
    // if(res == null){
    //   this.$message.error('请先登录！')
    //   //打回登录页
    //   this.$router.push('/login')
    // }
  },

  // 第二早，但是它可以访问到Data中的数据
  created() {
    //取出localStorage中保存的数据
    let obj = JSON.parse(window.localStorage.getItem("userInfo"));
    this.userInfo.name = obj.name;
    this.userInfo.photo = obj.photo;
  }
};
</script>

